import React, {useState} from 'react';  
import axios from 'axios';
import {navigate} from '@reach/router';

const Login = (props)=>{
  const [confirmLog, setConfirmLog] = useState("");
  const [errs, setErrs] = useState({});

  const [userLogin, setUserLogin] = useState({
    email: "",
    password:""
  })

  const handleChange = (e)=>{
    setUserLogin({
      ...userLogin,
      [e.target.name]: e.target.value
    })
  }

  const login = e =>{
    e.preventDefault();
    axios.post('http://localhost:8000/api/users/login',
    userLogin,
    {
      withCredentials: true,
    })
    .then((res)=>{
      console.log(res.data);
      setUserLogin({
        email:"",
        password:""
      })
      setConfirmLog("You are now logged in");
      setErrs({});
    })
    .catch((err)=>{
      console.log(err);
      setErrs(err.response.data.errors);
    })
};

return(
  <div>
    <h2>Login</h2>
    {
      confirmLog?
      <h1 style={{color: "grey"}}>{confirmLog}</h1>
      :null
    }
    <form onSubmit={login}>
      <div>
        <label>Email</label>
        {
          errs.email?
          <span className="error-text">{errs.email.message}</span>
          :null
        }
        <input type="email" name="email" value={userLogin.email} onChange={(e)=> handleChange(e)}/>
      </div>
      <div>
        <label>Password</label>
        {
          errs.password?
          <span className="error-text">{errs.password.message}</span>
          :null
        }
        <input type="password" name="password" value={userLogin.password} onChange={(e)=> handleChange(e)}/>
      </div>
      <button type="submit">Sign in</button>
    </form>
    </div>
);
};

export default Login;
