import React, {useState} from 'react';  
import axios from 'axios';

const Login = (props)=>{
  const [confirmLog, setConfirmLog] = useState("");
  const [errs, setErrs] = useState({});

  const [user, setUser] = useState({
    email: "",
    password:""
  })

  const handleChange = (e)=>{
    setUserLog({
      ...user,
      [e.target.name]: e.target.value
    })
  }

  const login = (e)=>{
    e.preventDefault();
    axios.post('http://localhost:8000/api/users/login',
    user,
    {
      withCredentials: true,
    })
    .then((res)=>{
      console.log(res.data);
      setUser({
        email:"",
        password:""
      })
      setConfirmLog("You are now logged in");
      setErrs({});
    })
    .catch((err)=>{
      console.log(err);
      setErrs(err.response.data.errors);
    })
};

return(
  <div>
    <h2>Login</h2>
    {
      confirmLog?
      <h1 style={{color: "grey"}}>{confirmLog}</h1>
      :null
    }
    <form onSubmit>{login}
      <div>
        <label>Email</label>
        {
          errs.email?
          <span className="error-text">{errs.email.message}</span>
          :null
        }
        <input type="email" name="email" value={user.email} onChange={(e)=> handleChange}/>
      </div>
      <div>
        <label>Password</label>
        {
          errs.password?
          <span className="error-text">{errs.password.message}</span>
          :null
        }
        <input type="password" name="password" value={user.password} onChange={(e)=> handleChange}/>
      </div>
      <button type="submit">Sign in</button>
    </form>
    </div>
);
};

export default Login;
